import axios from "axios";

const API_URL = "http://localhost:5000/api/borrowers";

export const getBorrowers = async () => {
    const response = await axios.get(API_URL);
    return response.data;
};

export const createBorrower = async (borrower) => {
    const response = await axios.post(API_URL, borrower);
    return response.data;
};

export const updateBorrower = async (id, borrower) => {
    const response = await axios.put(`${API_URL}/${id}`, borrower);
    return response.data;
};

export const deleteBorrower = async (id) => {
    const response = await axios.delete(`${API_URL}/${id}`);
    return response.data;
};
