import axios from "axios";
const API_URL = "http://localhost:5000/api/repayments";

export const getRepayments = async () => (await axios.get(API_URL)).data;
export const createRepayment = async (repayment) => (await axios.post(API_URL, repayment)).data;
export const getRepaymentById = async (id) => (await axios.get(`${API_URL}/${id}`)).data;
export const updateRepayment = async (id, repayment) => (await axios.put(`${API_URL}/${id}`, repayment)).data;
export const deleteRepayment = async (id) => (await axios.delete(`${API_URL}/${id}`)).data;
