import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { createBorrower, getBorrowerById, updateBorrower } from "../../api/borrowerApi";

const BorrowerForm = () => {
    const [borrower, setBorrower] = useState({ fullName: "", phone: "", email: "", address: "", idNumber: "" });
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        if (id) {
            getBorrowerById(id).then(setBorrower);
        }
    }, [id]);

    const handleChange = (e) => setBorrower({ ...borrower, [e.target.name]: e.target.value });
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (id) await updateBorrower(id, borrower);
        else await createBorrower(borrower);
        navigate("/borrowers");
    };

    return (
        <div className="max-w-md mx-auto">
            <h1 className="text-2xl font-bold mb-4">{id ? "Edit" : "Add"} Borrower</h1>
            <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow">
                <label className="block mb-2">Full Name</label>
                <input type="text" name="fullName" value={borrower.fullName} onChange={handleChange} className="w-full border p-2 mb-4" />
                <label className="block mb-2">Phone</label>
                <input type="text" name="phone" value={borrower.phone} onChange={handleChange} className="w-full border p-2 mb-4" />
                <label className="block mb-2">Email</label>
                <input type="email" name="email" value={borrower.email} onChange={handleChange} className="w-full border p-2 mb-4" />
                <label className="block mb-2">Address</label>
                <input type="text" name="address" value={borrower.address} onChange={handleChange} className="w-full border p-2 mb-4" />
                <label className="block mb-2">ID Number</label>
                <input type="text" name="idNumber" value={borrower.idNumber} onChange={handleChange} className="w-full border p-2 mb-4" />
                <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">{id ? "Update" : "Create"}</button>
            </form>
        </div>
    );
};

export default BorrowerForm;
