import { useEffect, useState } from "react";
import { getBorrowers, deleteBorrower } from "../../api/borrowerApi";
import { Link } from "react-router-dom";

const BorrowerList = () => {
    const [borrowers, setBorrowers] = useState([]);

    const fetchBorrowers = async () => setBorrowers(await getBorrowers());
    const handleDelete = async (id) => { await deleteBorrower(id); fetchBorrowers(); };

    useEffect(() => { fetchBorrowers(); }, []);

    return (
        <div>
            <h1 className="text-2xl font-bold mb-4">Borrowers</h1>
            <Link to="/borrowers/new" className="bg-blue-500 text-white px-4 py-2 rounded mb-4 inline-block">
                Add Borrower
            </Link>
            <table className="min-w-full bg-white border">
                <thead>
                    <tr>
                        <th className="py-2 px-4 border">Full Name</th>
                        <th className="py-2 px-4 border">Phone</th>
                        <th className="py-2 px-4 border">Email</th>
                        <th className="py-2 px-4 border">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {borrowers.map(b => (
                        <tr key={b._id}>
                            <td className="py-2 px-4 border">{b.fullName}</td>
                            <td className="py-2 px-4 border">{b.phone}</td>
                            <td className="py-2 px-4 border">{b.email}</td>
                            <td className="py-2 px-4 border">
                                <Link to={`/borrowers/edit/${b._id}`} className="text-blue-500 mr-2">Edit</Link>
                                <button onClick={() => handleDelete(b._id)} className="text-red-500">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default BorrowerList;
