import { useEffect, useState } from "react";
import { getContracts } from "../../api/contractApi";

const ContractList = () => {
    const [contracts, setContracts] = useState([]);

    useEffect(() => { getContracts().then(setContracts); }, []);

    return (
        <div>
            <h1 className="text-2xl font-bold mb-4">Contracts</h1>
            <table className="min-w-full bg-white border">
                <thead>
                    <tr>
                        <th className="py-2 px-4 border">Contract ID</th>
                        <th className="py-2 px-4 border">Loan</th>
                        <th className="py-2 px-4 border">Signing Date</th>
                        <th className="py-2 px-4 border">Document</th>
                    </tr>
                </thead>
                <tbody>
                    {contracts.map(c => (
                        <tr key={c._id}>
                            <td className="py-2 px-4 border">{c._id}</td>
                            <td className="py-2 px-4 border">{c.loan?.amount || c.loan}</td>
                            <td className="py-2 px-4 border">{new Date(c.signingDate).toLocaleDateString()}</td>
                            <td className="py-2 px-4 border">
                                <a href={`http://localhost:5000/${c.document}`} target="_blank" rel="noopener noreferrer" className="text-blue-500">View</a>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ContractList;
