import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { createLoan, getLoanById, updateLoan } from "../../api/loanApi";
import { getBorrowers } from "../../api/borrowerApi";

const LoanForm = () => {
    const [loan, setLoan] = useState({ borrower: "", amount: "", type: "", startDate: "", endDate: "", interestRate: "" });
    const [borrowers, setBorrowers] = useState([]);
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        getBorrowers().then(setBorrowers);
        if (id) getLoanById(id).then(setLoan);
    }, [id]);

    const handleChange = e => setLoan({ ...loan, [e.target.name]: e.target.value });
    const handleSubmit = async e => {
        e.preventDefault();
        if (id) await updateLoan(id, loan);
        else await createLoan(loan);
        navigate("/loans");
    };

    return (
        <div className="max-w-md mx-auto">
            <h1 className="text-2xl font-bold mb-4">{id ? "Edit" : "Add"} Loan</h1>
            <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow space-y-4">
                <label>Borrower</label>
                <select name="borrower" value={loan.borrower} onChange={handleChange} className="w-full border p-2">
                    <option value="">Select Borrower</option>
                    {borrowers.map(b => <option key={b._id} value={b._id}>{b.fullName}</option>)}
                </select>
                <label>Amount</label>
                <input type="number" name="amount" value={loan.amount} onChange={handleChange} className="w-full border p-2" />
                <label>Loan Type</label>
                <input type="text" name="type" value={loan.type} onChange={handleChange} className="w-full border p-2" />
                <label>Start Date</label>
                <input type="date" name="startDate" value={loan.startDate} onChange={handleChange} className="w-full border p-2" />
                <label>End Date</label>
                <input type="date" name="endDate" value={loan.endDate} onChange={handleChange} className="w-full border p-2" />
                <label>Interest Rate (%)</label>
                <input type="number" name="interestRate" value={loan.interestRate} onChange={handleChange} className="w-full border p-2" />
                <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">{id ? "Update" : "Create"}</button>
            </form>
        </div>
    );
};

export default LoanForm;
