import { useEffect, useState } from "react";
import { getLoans, deleteLoan } from "../../api/loanApi";
import { Link } from "react-router-dom";

const LoanList = () => {
    const [loans, setLoans] = useState([]);

    const fetchLoans = async () => setLoans(await getLoans());
    const handleDelete = async (id) => { await deleteLoan(id); fetchLoans(); };

    useEffect(() => { fetchLoans(); }, []);

    return (
        <div>
            <h1 className="text-2xl font-bold mb-4">Loans</h1>
            <Link to="/loans/new" className="bg-gray-500 text-white px-4 py-2 rounded mb-4 inline-block">Add Loan</Link>
            <table className="min-w-full bg-white border">
                <thead>
                    <tr>
                        <th className="py-2 px-4 border">Loan ID</th>
                        <th className="py-2 px-4 border">Borrower</th>
                        <th className="py-2 px-4 border">Amount</th>
                        <th className="py-2 px-4 border">Type</th>
                        <th className="py-2 px-4 border">Interest Rate</th>
                        <th className="py-2 px-4 border">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {loans.map(l => (
                        <tr key={l._id}>
                            <td className="py-2 px-4 border">{l._id}</td>
                            <td className="py-2 px-4 border">{l.borrower?.fullName || l.borrower}</td>
                            <td className="py-2 px-4 border">{l.amount}</td>
                            <td className="py-2 px-4 border">{l.type}</td>
                            <td className="py-2 px-4 border">{l.interestRate}%</td>
                            <td className="py-2 px-4 border">
                                <Link to={`/loans/edit/${l._id}`} className="text-gray-500 mr-2">Edit</Link>
                                <button onClick={() => handleDelete(l._id)} className="text-red-500">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default LoanList;
