import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { createRepayment } from "../../api/repaymentApi";
import { getLoans } from "../../api/loanApi";

const RepaymentForm = () => {
    const [repayment, setRepayment] = useState({ loan: "", paymentDate: "", amountPaid: "" });
    const [loans, setLoans] = useState([]);
    const navigate = useNavigate();

    useEffect(() => { getLoans().then(setLoans); }, []);

    const handleChange = e => setRepayment({ ...repayment, [e.target.name]: e.target.value });
    const handleSubmit = async e => { e.preventDefault(); await createRepayment(repayment); navigate("/repayments"); };

    return (
        <div className="max-w-md mx-auto">
            <h1 className="text-2xl font-bold mb-4">Add Repayment</h1>
            <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow space-y-4">
                <label>Loan</label>
                <select name="loan" value={repayment.loan} onChange={handleChange} className="w-full border p-2">
                    <option value="">Select Loan</option>
                    {loans.map(l => <option key={l._id} value={l._id}>{l.borrower?.fullName} - {l.amount}</option>)}
                </select>
                <label>Payment Date</label>
                <input type="date" name="paymentDate" value={repayment.paymentDate} onChange={handleChange} className="w-full border p-2" />
                <label>Amount Paid</label>
                <input type="number" name="amountPaid" value={repayment.amountPaid} onChange={handleChange} className="w-full border p-2" />
                <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Create</button>
            </form>
        </div>
    );
};

export default RepaymentForm;
