import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { createTransaction, getTransactionById, updateTransaction } from "../../api/transactionApi";
import { getLoans } from "../../api/loanApi";

const TransactionForm = () => {
    const [transaction, setTransaction] = useState({
        loan: "",
        type: "",
        amount: "",
        description: "",
        transactionDate: ""
    });

    const [loans, setLoans] = useState([]);
    const navigate = useNavigate();
    const { id } = useParams();

    const typeOptions = ["Repayment", "Late Fee", "Penalty"];

    useEffect(() => {
        // Fetch loans for dropdown
        getLoans().then(setLoans);

        // If editing, fetch transaction by ID
        if (id) {
            getTransactionById(id).then((data) => {
                setTransaction({
                    loan: data.loan?._id || "",
                    type: data.type || "",
                    amount: data.amount || "",
                    description: data.description || "",
                    transactionDate: data.transactionDate ? data.transactionDate.slice(0, 10) : ""
                });
            });
        }
    }, [id]);

    const handleChange = (e) => setTransaction({ ...transaction, [e.target.name]: e.target.value });

    const handleSubmit = async (e) => {
        e.preventDefault();

        const payload = {
            ...transaction,
            amount: Number(transaction.amount),
            transactionDate: transaction.transactionDate ? new Date(transaction.transactionDate) : new Date()
        };

        try {
            if (id) await updateTransaction(id, payload);
            else await createTransaction(payload);
            navigate("/transactions");
        } catch (err) {
            console.error(err);
            alert("Error saving transaction");
        }
    };

    return (
        <div className="max-w-md mx-auto">
            <h1 className="text-2xl font-bold mb-4">{id ? "Edit" : "Add"} Transaction</h1>
            <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow space-y-4">

                {/* Loan */}
                <div className="flex flex-col">
                    <label className="mb-1 font-medium">Loan</label>
                    <select
                        name="loan"
                        value={transaction.loan}
                        onChange={handleChange}
                        className="w-full border p-2 rounded"
                    >
                        <option value="">Select Loan</option>
                        {loans.map((l) => (
                            <option key={l._id} value={l._id}>
                                {l._id} - {l.borrower?.fullName || "Unknown"} - ${l.amount}
                            </option>
                        ))}
                    </select>
                </div>

                {/* Transaction Type */}
                <div className="flex flex-col">
                    <label className="mb-1 font-medium">Type</label>
                    <select
                        name="type"
                        value={transaction.type}
                        onChange={handleChange}
                        className="w-full border p-2 rounded"
                    >
                        <option value="">Select Type</option>
                        {typeOptions.map((t) => (
                            <option key={t} value={t}>{t}</option>
                        ))}
                    </select>
                </div>

                {/* Amount */}
                <div className="flex flex-col">
                    <label className="mb-1 font-medium">Amount</label>
                    <input
                        type="number"
                        name="amount"
                        value={transaction.amount}
                        onChange={handleChange}
                        className="w-full border p-2 rounded"
                    />
                </div>

                {/* Description */}
                <div className="flex flex-col">
                    <label className="mb-1 font-medium">Description</label>
                    <input
                        type="text"
                        name="description"
                        value={transaction.description}
                        onChange={handleChange}
                        className="w-full border p-2 rounded"
                    />
                </div>

                {/* Transaction Date */}
                <div className="flex flex-col">
                    <label className="mb-1 font-medium">Transaction Date</label>
                    <input
                        type="date"
                        name="transactionDate"
                        value={transaction.transactionDate}
                        onChange={handleChange}
                        className="w-full border p-2 rounded"
                    />
                </div>

                {/* Submit Button */}
                <div className="flex justify-end">
                    <button
                        type="submit"
                        className="bg-gray-500 text-white px-6 py-2 rounded hover:bg-gray-600"
                    >
                        {id ? "Update" : "Create"}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default TransactionForm;
