import { useEffect, useState } from "react";
import { getTransactions } from "../../api/transactionApi";

const TransactionList = () => {
    const [transactions, setTransactions] = useState([]);

    useEffect(() => { getTransactions().then(setTransactions); }, []);

    return (
        <div>
            <h1 className="text-2xl font-bold mb-4">Transactions</h1>
            <table className="min-w-full bg-white border">
                <thead>
                    <tr>
                        <th className="py-2 px-4 border">Transaction ID</th>
                        <th className="py-2 px-4 border">Loan</th>
                        <th className="py-2 px-4 border">Date</th>
                        <th className="py-2 px-4 border">Type</th>
                        <th className="py-2 px-4 border">Amount</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map(t => (
                        <tr key={t._id}>
                            <td className="py-2 px-4 border">{t._id}</td>
                            <td className="py-2 px-4 border">{t.loan?.amount || t.loan}</td>
                            <td className="py-2 px-4 border">{new Date(t.date).toLocaleDateString()}</td>
                            <td className="py-2 px-4 border">{t.type}</td>
                            <td className="py-2 px-4 border">{t.amount}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TransactionList;
